import React, {Component} from 'react';
import {BrowserRouter as Link} from 'react-router-dom';

import axios from 'axios';

import {User} from './index';

class Login extends Component {
    constructor() {
        super();
        
        this.state = {
            email: '',
            password: '',
            loggedInUser: {}
        };
    };

    onChange = (e) => {
        let {name, value} = e.target;
  
        this.setState({[name]: value});
     };

     login = () => {
         const {email, password} = this.state;
         //if these are not empty
        if (email && password) {
            // eslint-disable-next-line
            let loggedInUser = axios.post('/api/login', {email, password}).then(res=>res.data).catch(err=>console.log(err));
            this.setState(loggedInUser);
            //this is the user we are grabbing from the database, and you can now do whatever you want with the user information
        }
     };


    render() {
        return (
            <User.Consumer>
                {user=>
            <div >
            {this.state.loggedInUser ? (<>{user.setUser(this.state.loggedInUser)}</>) : (<></>)}
        {/*End preloader area*/}
    <header>
        {/* header-bottom */}
        <div className="header-bottom">
            <div className="container">
            <div className="row">
            <div className="col-md-3 col-xs-8">
            <div className="logo">
            <a href="/"><img src="img/logo.png" alt="logo" /></a>
        </div>
        </div>
        <div className="col-md-9 hidden-sm hidden-xs">
            <div className="mainmenu text-right">
            <nav>

        <ul id="navigation" style={{fontStyle: 'italic'}}>

            <li ><Link to="/">Home </Link></li>
            <li><Link to="/About">About Us</Link></li>
            <li><Link to="/Contact">Contact Us</Link></li>
            <li><Link to="/Account">Sign Up</Link></li>

            <li className="active"> <Link to ="/login" >Log In </Link> </li>

        </ul>

        </nav>
        </div>
        </div>
        <div className="col-xs-4 hidden-lg hidden-md">
            <div className="responsive-menu-wrap floatright" />
            </div>
            </div>
            </div>
            </div>
        {/* header-bottom */}
    </header>
        {/* header start */}

    <div className="account-area ptb-70 bg-2">
            <div className="container">
            <div className="row">
            <div className="col-md-12 ">
            <h3 className="sidebar-title" style={{textAlign: 'center'}}>Login</h3>
        <div className="checkout-form">
            <form action="#">
                <div className="row">
                    <div className="col-xs-12">
                        <span>Email</span>
                        <input type="text" placeholder="Your email here..."
                            onChange={(e)=>{this.onChange(e)}}
                        />
                    </div>
                    <div className="col-xs-12">
                        <span>Password</span>
                        <input type="password" placeholder="Your password here..."
                            onChange={(e)=>{this.onChange(e)}}
                        />
                    </div>
        <div className="col-xs-4 col-md-2">
            <button>Sign In</button>
        </div>
        </div>
        </form>
        </div>
        </div>
        </div>
        </div>
        </div> 
            </div>}
            </User.Consumer>
        );
    };
};

export default Login;
