import React from 'react';
import {BrowserRouter as Link} from 'react-router-dom';

import axios from 'axios';

//also this should be named register with the myAccount being the Account being the Account page...
//If i could make everything a functional component (with hooks) with much better syntax i would, This code was written by someone who does not know good coding practices at all.
class Account extends React.Component {
    constructor() {
        super();

        this.state = {
            username: '',
            email: '',
            first_name: '',
            last_name: '',
            password: '',
            passwordToConfirm: ''
        };
    };

    onChange = (e) => {
        let {name, value} = e.target;

        this.setState({[name]: value});
     };

     register = async() => {
        const {username, email, first_name, last_name, password, passwordToConfirm} = this.state;

        //if the passwords and the confirm your passwords are matching and the username, first_name, last_name, and password are not empty  create a call to our server.
        if (password === passwordToConfirm && username && first_name && last_name && password) {

            const user = await axios.post('/api/register', {username, email, first_name, last_name, password}).then(res=> res.data).catch(err=> console.log(err));
            console.log(user)
            //this is the user we are grabbing from the database, and you can now do whatever you want with the user information
        } else {
            //Whatever you want to happen if the 
        }
     };

    render() {
        return (
        <div>
            <header>
                <div className="header-bottom">
                    <div className="container">
                        <div className="row">
                            <div className="col-md-3 col-xs-8">
                                <div className="logo">
                                    <a href="/"><img src="img/logo.png" alt /></a>
                                </div>
                            </div>
                            <div className="col-md-9 hidden-sm hidden-xs">
                                <div className="mainmenu text-right">
                                    <nav>
                                        <ul id="navigation" style={{fontStyle: 'italic'}}>
                                            <li><Link to="/">Home </Link></li>
                                            <li><Link to="/About">About Us</Link></li>
                                            <li><Link to="/Contact">Contact Us</Link></li>
                                            <li className="active"><Link to="/Account">Sign Up</Link></li>
                                            <li> <Link to ="/login" >Log In</Link></li>
                                        </ul>
                                    </nav>
                                </div>
                            </div>
                            <div className="col-xs-4 hidden-lg hidden-md">
                                <div className="responsive-menu-wrap floatright"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </header>

      <div className="account-area ptb-70 bg-2">
              <div className="container">
              <div className="row">
              <div className="col-md-12 ">
              <h3 className="sidebar-title" style={{textAlign: 'center'}}>Register Your Account</h3>
          <div className="checkout-form">

              <div className="row">
              <div className="col-xs-6">
              <span>First Name <span className="star">*</span></span>
                <input 
                    type="text" 
                    placeholder="First Name" 
                    name="first_name"
                    onChange={(e)=>{this.onChange(e)}}
                />
              </div>
              <div className="col-xs-6">
              <span>Last Name <span className="star">*</span></span>
          <input 
            type="text" 
            placeholder="Last Name" 
            name="last_name"
            onChange={(e)=>{this.onChange(e)}}
          />
              </div>
              <div className="col-xs-6">
              <span>Username <span className="star">*</span></span>
          <input 
            type="text" 
            placeholder="Your username here..." 
            name="username"
            onChange={(e)=>{this.onChange(e)}}
          />
              </div>
              <div className="col-xs-6">
              <span>Email Address <span className="star">*</span></span>
          <input 
            type="email" 
            placeholder="Your email here..." 
            name="email"
            onChange={(e)=>{this.onChange(e)}}
          />
              </div>
              <div className="col-xs-6">
              <span>Password <span className="star">*</span></span>
          <input 
            type="password" 
            placeholder="Your password here..." 
            name="password"
            onChange={(e)=>{this.onChange(e)}}
          />
              </div>
              <div className="col-xs-6">
              <span>Repeat Password <span className="star">*</span></span>
          <input 
            type="password" 
            placeholder="Your password here..." 
            name="passwordToConfirm"
            onChange={(e)=>{this.onChange(e)}}
          />
              </div>
              <div className=" col-md-2 col-xs-4">
              <button onClick={()=>this.register()}>Register</button>
              </div>
              </div>

              </div>
              </div>
              </div>
              </div>
              </div>

      </div>

      );
    }
}

export default Account;
